field.action:
javascript:
			   var qtyBundle= parseInt(document.NewBundlingTxn1.quantityToBundle.value);
			   var qtyBundleRaw = document.NewBundlingTxn1.pendingQuantity.value;
			   var bundle = true;
			   var qtyPending = 0;
			   if (qtyBundleRaw.indexOf(',') != -1)
			   {
					qtyBundleRaw = qtyBundleRaw.replace(/,/g,'');
					qtyPending= parseInt(qtyBundleRaw);
			   }
			   else 
			   {
			   	qtyPending = parseInt(qtyBundleRaw);
			   }

			   if (isNaN(qtyBundle))
			    {
				alert('Invalid Value Specified. Only numeric value is allowed.');
				bundle = false;
				document.NewBundlingTxn1.quantityToBundle.focus();
			    }

			    if( qtyBundle > qtyPending)
				{
					alert('The Quantity to be bundled should be less or equal to the Quantity to make or there is no pending quantity left to bundle.');
					bundle= false;
					document.NewBundlingTxn1.quantityToBundle.focus();
				}
				return bundle;
